import React, { useState } from 'react';
import { Button, Container } from "react-bootstrap";
import CreateBrand from "../components/modals/CreateBrand";
import CreateDevice from "../components/modals/CreateDevice";
import GetResults from "../components/modals/GetResults";
import DeleteDevice from "../components/modals/DeleteDevice";

const Admin = () => {
  const [brandVisible, setBrandVisible] = useState(false);
  const [deviceVisible, setDeviceVisible] = useState(false);
  const [resultsVisible, setResultsVisible] = useState(false);
  const [deleteVisible, setDeleteVisible] = useState(false);

  return (
    <Container className="d-flex flex-column">
      <Button
        variant={"outline-dark"}
        className="mt-4 p-2"
        onClick={() => setBrandVisible(true)}
      >
        Добавить категорию
      </Button>
      <Button
        variant={"outline-dark"}
        className="mt-4 p-2"
        onClick={() => setDeviceVisible(true)}
      >
        Добавить опрос
      </Button>
      <Button
        variant={"outline-dark"}
        className="mt-4 p-2"
        onClick={() => setResultsVisible(true)}
      >
        Посмотреть результаты
      </Button>
      <Button
        variant={"outline-dark"}
        className="mt-4 p-2"
        onClick={() => setDeleteVisible(true)}
      >
        Удалить опрос
      </Button>
      <CreateBrand show={brandVisible} onHide={() => setBrandVisible(false)} />
      <CreateDevice show={deviceVisible} onHide={() => setDeviceVisible(false)} />
      <GetResults show={resultsVisible} onHide={() => setResultsVisible(false)} />
      <DeleteDevice show={deleteVisible} onHide={() => setDeleteVisible(false)} />
    </Container>
  );
};

export default Admin;
