import React, { useContext, useEffect, useState } from 'react';
import { Modal, Dropdown, Button, Row } from 'react-bootstrap';
import axios from 'axios';
import { Context } from '../..';

const GetResults = ({ show, onHide }) => {
  const [info, setInfo] = useState([]);
  const [results, setResults] = useState({});
  const { device } = useContext(Context);

  useEffect(() => {
    let integer = device.selectedDevice.id;
    axios.get(`${process.env.REACT_APP_API_URL}api/testResult/${integer}`)
      .then(response => {
        setResults(response.data);
      })
      .catch(error => {
        console.error('There was an error fetching the survey results!', error);
      });
  }, [device.devices]); // Добавляем зависимость от device.devices

  const getResult = () => {
    console.log(results); // Проверяем содержимое results
    if (results && Array.isArray(results.answers)) {
      setInfo([...info, {
        description1: results.answers[0] || 'No description1',
        description2: results.answers[1] || 'No description2',
        description3: results.answers[2] || 'No description3',
        description4: results.answers[3] || 'No description4',
        number: Date.now()
      }]);
    } else {
      console.error('Results are empty or invalid');
    }
  };

  return (
    <Modal
      show={show}
      onHide={onHide}
      centered
    >
      <h2>Survey Results</h2>
      <Dropdown className="mt-2 mb-2">
        <Dropdown.Toggle>{device.selectedDevice.name || "Выберите опрос"}</Dropdown.Toggle>
        <Dropdown.Menu>
          {Array.isArray(device.devices) && device.devices.map(deviceItem =>
            <Dropdown.Item
              onClick={() => device.setSelectedDevice(deviceItem)}
              key={deviceItem.id}
            >
              {deviceItem.name}
            </Dropdown.Item>
          )}
        </Dropdown.Menu>
      </Dropdown>
      <Modal.Footer>
        <Button variant="outline-danger" onClick={onHide}>Закрыть</Button>
        <Button variant="outline-success" onClick={getResult}>Посмотреть</Button>
      </Modal.Footer>
      {info.map(i =>
        <Row className="mt-2 mb-2" key={i.number}>
          <div>{i.title}</div>
          <div>{i.description1}</div>
          <div>{i.description2}</div>
          <div>{i.description3}</div>
          <div>{i.description4}</div>
        </Row>
      )}
    </Modal>
  );
};

export default GetResults;
