import React, {useContext, useState, useEffect} from 'react';
import {Button, Dropdown, Form, Col, Modal} from "react-bootstrap";
import {Context} from "../../index";
import {createDevice, fetchCounts} from "../../http/deviceAPI";
import {observer} from "mobx-react-lite";

const CreateDevice = observer(({show, onHide}) => {
    const {device} = useContext(Context)
    const [name, setName] = useState('')
    const [info, setInfo] = useState([])

    useEffect(() => {
        fetchCounts().then(data => device.setCounts(data))
    }, [])

    const addInfo = () => {
        setInfo([...info, {title: '', count: '', description1: '', description2: '', description3: '', description4: '', correctAnswers: [], number: Date.now()}])
    }
    const removeInfo = (number) => {
        setInfo(info.filter(i => i.number !== number))
    }
    const changeInfo = (key, value, number) => {
        setInfo(info.map(i => i.number === number ? {...i, [key]: value} : i))
    }
    const changeCorrectAnswers = (value, number) => {
        setInfo(info.map(i => i.number === number ? {...i, correctAnswers: [...i.correctAnswers, value]} : i))
    }

    const addDevice = () => {
        const formData = new FormData()
        formData.append('name', name)
        formData.append('countId', device.selectedCount.id)
        formData.append('info', JSON.stringify(info))
        createDevice(formData).then(data => onHide())
    }

    return (
        <Modal
            show={show}
            onHide={onHide}
        >
            <Modal.Header closeButton>
                <Modal.Title>Добавить устройство</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form>
                    <Form.Control
                        value={name}
                        onChange={e => setName(e.target.value)}
                        className="mt-3"
                        placeholder="Введите название устройства"
                    />
                    <Dropdown className="mt-3 mb-2">
                        <Dropdown.Toggle>{device.selectedCount.name || "Выберите категорию"}</Dropdown.Toggle>
                        <Dropdown.Menu>
                            {device.counts.map(count =>
                                <Dropdown.Item
                                    onClick={() => device.setSelectedCount(count)}
                                    key={count.id}
                                >
                                    {count.name}
                                </Dropdown.Item>
                            )}
                        </Dropdown.Menu>
                    </Dropdown>
                    <Button
                        variant={"outline-dark"}
                        onClick={addInfo}
                    >
                        Добавить новое свойство
                    </Button>
                    {info.map(i =>
                        <Col key={i.number} className="mt-4">
                            <Form.Control
                                value={i.title}
                                onChange={(e) => changeInfo('title', e.target.value, i.number)}
                                className="mt-2 mb-2"
                                placeholder="Введите название свойства"
                            />
                            <Form.Control
                                value={i.description1}
                                onChange={(e) => changeInfo('description1', e.target.value, i.number)}
                                className="mt-2 mb-2"
                                placeholder="Введите варинты ответа(1)"
                            />
                            <Form.Control
                                value={i.description2}
                                onChange={(e) => changeInfo('description2', e.target.value, i.number)}
                                className="mt-2 mb-2"
                                placeholder="Введите варинты ответа(2)"
                            />
                            <Form.Control
                                value={i.description3}
                                onChange={(e) => changeInfo('description3', e.target.value, i.number)}
                                className="mt-2 mb-2"
                                placeholder="Введите варинты ответа(3)"
                            />
                            <Form.Control
                                value={i.description4}
                                onChange={(e) => changeInfo('description4', e.target.value, i.number)}
                                className="mt-2 mb-2"
                                placeholder="Введите варинты ответа(4)"
                            />
                            <Form.Control as="select" className="mt-2 mb-2" onChange={(e) => changeCorrectAnswers(e.target.value, i.number)}>
                                <option value="">Выберите правильный ответ</option>
                                <option value="description1">Ответ 1</option>
                                <option value="description2">Ответ 2</option>
                                <option value="description3">Ответ 3</option>
                                <option value="description4">Ответ 4</option>
                            </Form.Control>
                            <Button
                                onClick={() => removeInfo(i.number)}
                                variant={"outline-danger"}
                                className="mt-2 mb-2"
                            >
                                Удалить
                            </Button>
                        </Col>
                    )}
                </Form>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="outline-danger" onClick={onHide}>Закрыть</Button>
                <Button variant="outline-success" onClick={addDevice}>Добавить</Button>
            </Modal.Footer>
        </Modal>
    );
});

export default CreateDevice;
