import React, { useEffect, useState } from 'react';
import { Col, Container, Row } from "react-bootstrap";
import { useParams } from 'react-router-dom';
import { fetchOneDevice } from "../http/deviceAPI";

const DevicePage = () => {
  const [device, setDevice] = useState({ info: [] });
  const [selectedDescription, setSelectedDescription] = useState('');
  const { id } = useParams();

  useEffect(() => {
    fetchOneDevice(id).then(data => setDevice(data));
  }, [id]);

  const handleSelection = (event) => {
    setSelectedDescription(event.target.value);
  };

  return (
    <Container className="mt-3">
      <Row className="d-flex flex-column m-3">
        <h1>{device.name}</h1>
        {device.info.map((info, index) => (
            <>
            <Row 
                key={info.id} 
                style={{ background: index % 2 === 0 ? 'lightgray' : 'transparent', padding: 10 }}
            >
                {info.title}
            </Row>
            <Col>
                {[info.description1, info.description2, info.description3, info.description4]
                    .filter(Boolean) // Исключаем пустые описания
                    .map((description, idx) => (
                    <div key={idx}>
                        <input 
                        type="radio" 
                        name="description" 
                        value={description}
                        id={`desc-${info.id}-${idx}`} 
                        checked={selectedDescription === description}
                        onChange={handleSelection}
                        />
                        <label htmlFor={`desc-${info.id}-${idx}`} style={{ marginLeft: '8px' }}>
                        {description}
                        </label>
                    </div>
                    ))}
            </Col>
            </>
        ))}
      </Row>
    </Container>
  );
};

export default DevicePage;
