import React, {useState, useEffect} from 'react';
import {Container, Row, Col, Button} from "react-bootstrap";
import {useParams} from 'react-router-dom';
import {fetchOneDevice} from "../http/deviceAPI";
import {createTestResult} from "../http/testResultAPI";

const DevicePage = () => {
    const {id} = useParams()
    const [device, setDevice] = useState({info: []})
    const [answers, setAnswers] = useState({})

    useEffect(() => {
        fetchOneDevice(id).then(data => setDevice(data))
    }, [])

    const handleAnswerChange = (index, value) => {
        setAnswers({...answers, [index]: value})
    }

    const handleSubmit = () => {
        const userId = 1 // Замените на реальный userId
        createTestResult({deviceId: id, userId, answers}).then(data => {
            console.log('Ответы сохранены', data)
        })
    }

    return (
        <Container className="mt-3">
            <Row className="d-flex flex-column m-3">
                <h1>{device.name}</h1>
                {device.info.map((info, index) => (
                    info.count === 'один вариант ответа' ? (
                <Col key={info.id} className="mt-4">
                    <Row style={{background:'lightgray', padding: 10}}>
                        {index + 1} {info.title} "{info.count}"
                    </Row>
                    <Row>
                        <input type='radio' name={index} value="description1" onChange={(e) => handleAnswerChange(index, e.target.value)} />
                        {info.description1}
                    </Row>
                    <Row>
                        <input type='radio' name={index} value="description2" onChange={(e) => handleAnswerChange(index, e.target.value)} />
                        {info.description2}
                    </Row>
                    <Row>
                        <input type='radio' name={index} value="description3" onChange={(e) => handleAnswerChange(index, e.target.value)} />
                        {info.description3}
                    </Row>
                    <Row>
                        <input type='radio' name={index} value="description4" onChange={(e) => handleAnswerChange(index, e.target.value)} />
                        {info.description4}
                    </Row>
                </Col>
                ) : (
                    <Col key={info.id} className="mt-4">
                    <Row style={{background:'lightgray', padding: 10}}>
                        {index + 1} {info.title} "{info.count}"
                    </Row>
                    <Row>
                        <input type='radio' value="description1" onChange={(e) => handleAnswerChange(index, e.target.value)} />
                        {info.description1}
                    </Row>
                    <Row>
                        <input type='radio' value="description2" onChange={(e) => handleAnswerChange(index, e.target.value)} />
                        {info.description2}
                    </Row>
                    <Row>
                        <input type='radio' value="description3" onChange={(e) => handleAnswerChange(index, e.target.value)} />
                        {info.description3}
                    </Row>
                    <Row>
                        <input type='radio' value="description4" onChange={(e) => handleAnswerChange(index, e.target.value)} />
                        {info.description4}
                    </Row>
                    </Col>
                    )
                ))}
                )}
            </Row>
            <Button onClick={handleSubmit}>Отправить</Button>
        </Container>
    );
};

export default DevicePage;
