import React, {useContext, useState, useEffect} from 'react';
import Modal from "react-bootstrap/Modal";
import {Button, Dropdown, Form, Col, Row} from "react-bootstrap";
import {Context} from "../../index";
import {createDevice, fetchCounts} from "../../http/deviceAPI";
import {observer} from "mobx-react-lite";

const CreateDevice = observer(({show, onHide}) => {
    const {device} = useContext(Context)
    const [name, setName] = useState('')
    const [info, setInfo] = useState([])

    useEffect(() => {
        fetchCounts().then(data => device.setCounts(data))
    }, [])

    const addInfo = () => {
        setInfo([...info, {title: '', description1: '', description2: '', description3: '', description4: '', rightAnswers: [], number: Date.now()}])
    }
    const removeInfo = (number) => {
        setInfo(info.filter(i => i.number !== number))
    }
    const changeInfo = (key, value, number) => {
        setInfo(info.map(i => i.number === number ? {...i, [key]: value} : i))
    }

    const handleCheckboxChange = (value, number) => {
        setInfo(info.map(i => {
            if (i.number === number) {
                const newRightAnswers = i.rightAnswers.includes(value)
                    ? i.rightAnswers.filter(answer => answer !== value)
                    : [...i.rightAnswers, value];
                return {...i, rightAnswers: newRightAnswers};
            }
            return i;
        }));
    }

    const addDevice = () => {
        const formData = new FormData()
        formData.append('name', name)
        formData.append('brandId', device.selectedBrand.id)
        formData.append('info', JSON.stringify(info))
        createDevice(formData).then(data => onHide())
    }

    return (
        <Modal
            show={show}
            onHide={onHide}
            centered
        >
            <Modal.Header closeButton>
                <Modal.Title id="contained-modal-title-vcenter">
                    Добавить опрос
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form>
                    <Dropdown className="mt-2 mb-2">
                        <Dropdown.Toggle>{device.selectedBrand.name || "Выберите категорию"}</Dropdown.Toggle>
                        <Dropdown.Menu>
                            {device.brands.map(brand =>
                                <Dropdown.Item
                                    onClick={() => device.setSelectedBrand(brand)}
                                    key={brand.id}
                                >
                                    {brand.name}
                                </Dropdown.Item>
                            )}
                        </Dropdown.Menu>
                    </Dropdown>
                    <Form.Control
                        value={name}
                        onChange={e => setName(e.target.value)}
                        className="mt-3"
                        placeholder="Введите название опроса"
                    />
                    <hr/>
                    <Button
                        variant={"outline-dark"}
                        onClick={addInfo}
                    >
                        Добавить новый вопрос
                    </Button>
                    {info.map((i, index) =>
                        <Col className="mt-4" key={i.number}>
                            <Col className="mt-2 mb-2">
                                <Dropdown className="mt-2 mb-2">
                                    <Dropdown.Toggle>{i.count || "Выберите тип"}</Dropdown.Toggle>
                                    <Dropdown.Menu>
                                    {device.counts.map(count => 
                                        <Dropdown.Item
                                            onClick={(e) => {
                                                device.setSelectedCount(count);
                                                changeInfo('count', count.name, i.number);
                                            }}
                                            key={count.id}
                                        >
                                            {count.name}
                                        </Dropdown.Item>
                                    )}
                                    </Dropdown.Menu>
                                </Dropdown>
                            </Col>
                            <Col className="mt-2 mb-2">
                            <Form.Control
                                    value={i.title}
                                    onChange={(e) => changeInfo('title', e.target.value, i.number)}
                                    placeholder="Введите вопрос"
                                />
                            </Col>
                            <Col className="mt-2 mb-2">
                                <Form.Control
                                    value={i.description1}
                                    onChange={(e) => changeInfo('description1', e.target.value, i.number)}
                                    placeholder="Введите варинты ответа(1)"
                                />
                            </Col>
                            <Col className="mt-2 mb-2">
                                <Form.Control
                                    value={i.description2}
                                    onChange={(e) => changeInfo('description2', e.target.value, i.number)}
                                    placeholder="Введите варинты ответа(2)"
                                />
                            </Col>
                            <Col className="mt-2 mb-2">
                                <Form.Control
                                    value={i.description3}
                                    onChange={(e) => changeInfo('description3', e.target.value, i.number)}
                                    placeholder="Введите варинты ответа(3)"
                                />
                            </Col>
                            <Col className="mt-2 mb-2">
                                <Form.Control
                                    value={i.description4}
                                    onChange={(e) => changeInfo('description4', e.target.value, i.number)}
                                    placeholder="Введите варинты ответа(4)"
                                />
                            </Col>
                            <Col>
                                {i.count === 'несколько вариантов ответа' ? (
                                    <>
                                        <Row>
                                            <input type='checkbox' value="description1" onChange={(e) => handleCheckboxChange('description1', i.number)} />
                                            {i.description1}
                                        </Row>
                                        <Row>
                                            <input type='checkbox' value="description2" onChange={(e) => handleCheckboxChange('description2', i.number)} />
                                            {i.description2}
                                        </Row>
                                        <Row>
                                            <input type='checkbox' value="description3" onChange={(e) => handleCheckboxChange('description3', i.number)} />
                                            {i.description3}
                                        </Row>
                                        <Row>
                                            <input type='checkbox' value="description4" onChange={(e) => handleCheckboxChange('description4', i.number)} />
                                            {i.description4}
                                        </Row>
                                    </>
                                ) : (
                                    <>
                                    <Row>
                                        <input type='radio' name={index} value="description1" onChange={(e) => changeInfo('rightAnswers', 'description1', i.number)} />
                                        {i.description1}
                                    </Row>
                                    <Row>
                                        <input type='radio' name={index} value="description2" onChange={(e) => changeInfo('rightAnswers', 'description2', i.number)} />
                                        {i.description2}
                                    </Row>
                                    <Row>
                                        <input type='radio' name={index} value="description3" onChange={(e) => changeInfo('rightAnswers', 'description3', i.number)} />
                                        {i.description3}
                                    </Row>
                                    <Row>
                                        <input type='radio' name={index} value="description4" onChange={(e) => changeInfo('rightAnswers', 'description4', i.number)} />
                                        {i.description4}
                                    </Row>
                                </>
                                )}
                            </Col>
                            <Col className="mt-2 mb-2">
                                <Button
                                    onClick={() => removeInfo(i.number)}
                                    variant={"outline-danger"}
                                >
                                    Удалить
                                </Button>
                            </Col>
                        </Col>
                    )}
                </Form>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="outline-danger" onClick={onHide}>Закрыть</Button>
                <Button variant="outline-success" onClick={addDevice}>Добавить</Button>
            </Modal.Footer>
        </Modal>
    );
});

export default CreateDevice;
