import React, {useContext, useEffect, useState} from 'react';
import Modal from "react-bootstrap/Modal";
import {Button, Dropdown, Form, Col} from "react-bootstrap";
import {Context} from "../../index";
import {createDevice, fetchBrands, fetchCounts} from "../../http/deviceAPI";
import {observer} from "mobx-react-lite";

const CreateDevice = observer(({show, onHide}) => {
    const {device} = useContext(Context)
    const [name, setName] = useState('')
    const [info, setInfo] = useState([])

    useEffect(() => {
        fetchBrands().then(data => device.setBrands(data))
        fetchCounts().then(data => device.setCounts(data))
    }, [])

    const addInfo = () => {
        setInfo([...info, {title: '', description1: '', description2: '', description3: '', description4: '', number: Date.now()}])
    }
    const removeInfo = (number) => {
        setInfo(info.filter(i => i.number !== number))
    }
    const changeInfo = (key, value, number) => {
        setInfo(info.map(i => i.number === number ? {...i, [key]: value} : i))
    }
    const changeCorrectAnswers = (value, number) => {
        setInfo(info.map(i => i.number === number ? {...i, correctAnswers: [...i.correctAnswers, value]} : i))
    }

    const addDevice = () => {
        const formData = new FormData()
        formData.append('name', name)
        formData.append('brandId', device.selectedBrand.id)
        formData.append('info', JSON.stringify(info))
        createDevice(formData).then(data => onHide())
    }

    return (
        <Modal
            show={show}
            onHide={onHide}
            centered
        >
            <Modal.Header closeButton>
                <Modal.Title id="contained-modal-title-vcenter">
                    Добавить опрос
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form>
                    <Dropdown className="mt-2 mb-2">
                        <Dropdown.Toggle>{device.selectedBrand.name || "Выберите категорию"}</Dropdown.Toggle>
                        <Dropdown.Menu>
                            {device.brands.map(brand =>
                                <Dropdown.Item
                                    onClick={() => device.setSelectedBrand(brand)}
                                    key={brand.id}
                                >
                                    {brand.name}
                                </Dropdown.Item>
                            )}
                        </Dropdown.Menu>
                    </Dropdown>
                    <Form.Control
                        value={name}
                        onChange={e => setName(e.target.value)}
                        className="mt-3"
                        placeholder="Введите название опроса"
                    />
                    <hr/>
                    <Button
                        variant={"outline-dark"}
                        onClick={addInfo}
                    >
                        Добавить новый вопрос
                    </Button>
                    {info.map(i =>
                        <Col key={i.number} className="mt-4">
                            <Form.Control
                                value={i.title}
                                onChange={(e) => changeInfo('title', e.target.value, i.number)}
                                className="mt-2 mb-2"
                                placeholder="Введите название свойства"
                            />
                            <Form.Control
                                value={i.description1}
                                onChange={(e) => changeInfo('description1', e.target.value, i.number)}
                                className="mt-2 mb-2"
                                placeholder="Введите варинты ответа(1)"
                            />
                            <Form.Control
                                value={i.description2}
                                onChange={(e) => changeInfo('description2', e.target.value, i.number)}
                                className="mt-2 mb-2"
                                placeholder="Введите варинты ответа(2)"
                            />
                            <Form.Control
                                value={i.description3}
                                onChange={(e) => changeInfo('description3', e.target.value, i.number)}
                                className="mt-2 mb-2"
                                placeholder="Введите варинты ответа(3)"
                            />
                            <Form.Control
                                value={i.description4}
                                onChange={(e) => changeInfo('description4', e.target.value, i.number)}
                                className="mt-2 mb-2"
                                placeholder="Введите варинты ответа(4)"
                            />
                            {device.selectedCount.name === 'несколько вариантов ответа' ? (
                                <Form.Group>
                                    <Form.Check
                                        type="checkbox"
                                        label="Ответ 1"
                                        value="description1"
                                        onChange={(e) => changeCorrectAnswers(e.target.value, i.number)}
                                    />
                                    <Form.Check
                                        type="checkbox"
                                        label="Ответ 2"
                                        value="description2"
                                        onChange={(e) => changeCorrectAnswers(e.target.value, i.number)}
                                    />
                                    <Form.Check
                                        type="checkbox"
                                        label="Ответ 3"
                                        value="description3"
                                        onChange={(e) => changeCorrectAnswers(e.target.value, i.number)}
                                    />
                                    <Form.Check
                                        type="checkbox"
                                        label="Ответ 4"
                                        value="description4"
                                        onChange={(e) => changeCorrectAnswers(e.target.value, i.number)}
                                    />
                                </Form.Group>
                            ) : (
                                <Form.Control
                                    as="select"
                                    value={i.correctAnswers[0]}
                                    onChange={(e) => changeInfo('correctAnswers', [e.target.value], i.number)}
                                    className="mt-2 mb-2"
                                >
                                    <option value="">Выберите правильный ответ</option>
                                    <option value="description1">Ответ 1</option>
                                    <option value="description2">Ответ 2</option>
                                    <option value="description3">Ответ 3</option>
                                    <option value="description4">Ответ 4</option>
                                </Form.Control>
                            )}
                                <Button
                                    onClick={() => removeInfo(i.number)}
                                    variant={"outline-danger"}
                                >
                                    Удалить
                                </Button>
                        </Col>
                    )}
                </Form>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="outline-danger" onClick={onHide}>Закрыть</Button>
                <Button variant="outline-success" onClick={addDevice}>Добавить</Button>
            </Modal.Footer>
        </Modal>
    );
});

export default CreateDevice;
