import React, { useContext, useEffect, useState } from 'react';
import { Modal, Dropdown, Button, Row } from 'react-bootstrap';
import axios from 'axios';
import { Context } from '../..';

const GetResults = ({ show, onHide }) => {
  const [results, setResults] = useState([]);
  const { device } = useContext(Context);

  useEffect(() => {
    if (device.selectedDevice.id) {
      axios.get(`http://localhost:5000/api/testResult?deviceId=${device.selectedDevice.id}`)
        .then(response => {
          const filteredResults = response.data.filter(result => result.deviceId === 42);
          setResults(filteredResults);
        })
        .catch(error => {
          console.error('There was an error fetching the survey results!', error);
        });
    }
  }, [device.selectedDevice]);

  const getStatistics = () => {
    const statistics = {};

    if (results && Array.isArray(results)) {
      results.forEach(result => {
        if (result.answers && Array.isArray(result.answers)) {
          result.answers.forEach((answer, index) => {
            if (!statistics[index]) {
              statistics[index] = {};
            }
            if (!statistics[index][answer]) {
              statistics[index][answer] = 0;
            }
            statistics[index][answer]++;
          });
        } else {
          console.warn('Missing or invalid answers for result:', result);
        }
      });
    }

    return statistics;
  };

  const handleGetStatistics = () => {
    const statistics = getStatistics();
    console.log('Statistics:', statistics);
  };


  return (
    <Modal
      show={show}
      onHide={onHide}
      centered
    >
      <h2>Survey Results</h2>
      <Dropdown className="mt-2 mb-2">
        <Dropdown.Toggle>{device.selectedDevice.name || "Выберите опрос"}</Dropdown.Toggle>
        <Dropdown.Menu>
          {Array.isArray(device.devices) && device.devices.map(deviceItem =>
            <Dropdown.Item
              onClick={() => device.setSelectedDevice(deviceItem)}
              key={deviceItem.id}
            >
              {deviceItem.name}
            </Dropdown.Item>
          )}
        </Dropdown.Menu>
      </Dropdown>
      <Modal.Footer>
        <Button variant="outline-danger" onClick={onHide}>Закрыть</Button>
        <Button variant="outline-success" onClick={handleGetStatistics}>Посмотреть статистику</Button>
      </Modal.Footer>
      <div>
        {Object.keys(statistics).map(questionIndex => (
          <div key={questionIndex}>
            <h3>Вопрос {parseInt(questionIndex) + 1}</h3>
            {Object.keys(statistics[questionIndex]).map(answer => (
              <div key={answer}>
                - {answer} – {statistics[questionIndex][answer]} ответов
              </div>
            ))}
          </div>
        ))}
      </div>
    </Modal>
  );
};

export default GetResults;
